import React, { useState } from 'react';
import './App.css';

function App() {
  const [articles, setArticles] = useState(null);
  const [searchInput, setSearchInput] = useState('tesla');

  function handleClick() {
    getData(searchInput);

    async function getData(search) {
      const response = await fetch(
        `https://free-news.p.rapidapi.com/v1/search?q=${search}&lang=en&page_size=2`,
        {
          method: 'GET',
          headers: {
            'x-rapidapi-host': 'free-news.p.rapidapi.com',
            'x-rapidapi-key':
              '99904098f4msh9256c1370a1059dp15ba00jsn7a23af031e17',
          },
        }
      );
      const data = await response.json();
      setArticles(data.articles);
      console.log(data.articles);
    }
  }

  return (
    <div className='App'>
      <input
        type='text'
        className='search'
        value={searchInput}
        onChange={(e) => setSearchInput(e.target.value)}
      />
      <button onClick={handleClick()}>Search</button>
      <main>
        {articles && (
          <div className='articles'>
            {articles.map((article, index) => (
              <article key={index}>
                <img src={article.media} alt='article' width={400} />
                <div className='text'>
                  <h3>{article.title}</h3>
                  <p className='summary'>{article.summary}</p>
                  <a
                    className='link'
                    href={article.link}
                    target='_blank'
                    rel='noreferrer'
                  >
                    Full Story
                  </a>
                </div>
              </article>
            ))}
            ;
          </div>
        )}
      </main>
    </div>
  );
}

export default App;
