import React, { useState, useEffect } from 'react';
import './App.css';
import { Link } from 'react-router-dom';

function App() {
  const [articles, setArticles] = useState(null);
  const [search, setSearch] = useState('tesla');

  async function getData() {
    const key = '85062f74bccf4b33872bf17c11caaae6';
    const url = `https://newsapi.org/v2/everything?language=en&q=${search}&from=2021-10-20&sortBy=publishedAt&apiKey=${key}`;

    const response = await fetch(url);
    const data = await response.json();

    setArticles(data.articles);
    console.log(data.articles);
  }

  return (
    <div className='App'>
      <div class='search'>
        <input
          type='search'
          value={search}
          onChange={(e) => setSearch(e.target.value)}
          className='searchInput'
        />
        <button onClick={getData()}>Search</button>
      </div>
      <main>
        {articles && (
          <div className='articles'>
            {articles.map((article, index) => (
              <article key={index}>
                <img src={article.urlToImage} alt='article' width={400} />
                <div className='text'>
                  <h3>{article.title}</h3>
                  <p>{article.description}</p>
                  <Link to={`/fullstory/${index}`}>Full Story</Link>
                </div>
              </article>
            ))}
            ;
          </div>
        )}
      </main>
    </div>
  );
}

export default App;
