import React from 'react';
import './FullStory.css';
import { RiTwitterLine } from 'react-icons/ri';

function FullStory({ article }) {
  console.log(article);
  return (
    <div className='fullStory'>
      <a
        class='twitter-share-button'
        href={`https://twitter.com/intent/tweet?text=${article.title}#news%20@afloreztweets&url=${article.url}`}
        target='_blank'
        rel='noreferrer'
      >
        <RiTwitterLine className='icon' />
      </a>
      <img
        className='fullStoryImage'
        src={article.urlToImage}
        alt='Story Media'
      />
      <h2 className='fullStoryTitle'>{article.title}</h2>
      <p className='fullStorySummary'>{article.description}</p>
      <div className='right'>
        <p>Full article at: </p>
        <a
          className='fullStoryLink'
          href={article.url}
          target='_blank'
          rel='noreferrer'
        >
          {article.source.name}
        </a>
      </div>
    </div>
  );
}

export default FullStory;
