import React, { useState, useEffect } from 'react';
import './App.css';

function App() {
  const [articles, setArticles] = useState(null);
  const [searchInput, setSearchInput] = useState('tesla');

  useEffect(() => {
    getData();
  }, []);

  async function getData() {
    // const url = `https://newsapi.org/v2/everything?language=en&q=technology&from=2021-10-20&sortBy=publishedAt&apiKey=85062f74bccf4b33872bf17c11caaae6`;

    const response = await fetch(
      `https://free-news.p.rapidapi.com/v1/search?q=programming&lang=en&page_size=10`,
      {
        method: 'GET',
        headers: {
          'x-rapidapi-host': 'free-news.p.rapidapi.com',
          'x-rapidapi-key':
            '99904098f4msh9256c1370a1059dp15ba00jsn7a23af031e17',
        },
      }
    );
    const data = await response.json();
    setArticles(data.articles);
    console.log(data.articles);
  }
  function handleChange(e) {
    setSearchInput(e.target.value);
  }

  function handleClick() {}

  return (
    <div className='App'>
      <input type='text' className='search' onChange={handleChange()} />
      <button onclick={handleClick()}>Search</button>
      <main>
        {articles && (
          <div className='articles'>
            {articles.map((article, index) => (
              <article key={index}>
                <img src={article.media} alt='article' width={400} />
                <div className='text'>
                  <h3>{article.title}</h3>
                  <p className='summary'>{article.summary}</p>
                  <a
                    className='link'
                    href={article.link}
                    target='_blank'
                    rel='noreferrer'
                  >
                    Full Story
                  </a>
                </div>
              </article>
            ))}
            ;
          </div>
        )}
      </main>
    </div>
  );
}

export default App;
