import React, { useState } from 'react';
import './Stories.css';
import { Link } from 'react-router-dom';
function Stories({ setArticle, searchedArticles }) {
  const [articles, setArticles] = useState([]);

  setArticles(searchedArticles);

  if (articles.length < 1) {
    return (
      <div className='stories'>
        <div className='centeredText'>
          <div className='centeredImg'></div>
          <h3>Type something in the search bar!</h3>
        </div>
      </div>
    );
  } else {
    return (
      <div className='stories'>
        {articles && (
          <div className='articles'>
            {articles.map((article, index) => (
              <>
                <article key={index}>
                  <img src={article.media} alt='article' width={400} />
                  <div className='text'>
                    <h3>{article.title}</h3>
                    <p className='summary'>{article.summary}</p>
                    <Link
                      to={`/fullstory`}
                      className='link'
                      onClick={(e) => setArticle(article)}
                    >
                      Full Story
                    </Link>
                  </div>
                </article>
              </>
            ))}
          </div>
        )}
      </div>
    );
  }
}

export default Stories;
