import React, { useState, useEffect } from 'react';
import './App.css';
import { Link } from 'react-router-dom';

function App() {
  const [articles, setArticles] = useState(null);
  const [search, setSearch] = useState(null);

  useEffect(() => {
    getData();

    async function getData() {
      // const url = `https://newsapi.org/v2/everything?language=en&q=technology&from=2021-10-20&sortBy=publishedAt&apiKey=85062f74bccf4b33872bf17c11caaae6`;

      const response = await fetch(
        'https://free-news.p.rapidapi.com/v1/search?q=Elon%20Musk&lang=en&page_size=10',
        {
          method: 'GET',
          headers: {
            'x-rapidapi-host': 'free-news.p.rapidapi.com',
            'x-rapidapi-key':
              '99904098f4msh9256c1370a1059dp15ba00jsn7a23af031e17',
          },
        }
      );
      const data = await response.json();

      setArticles(data.articles);
    }
  }, []);

  return (
    <div className='App'>
      <div className='search'>
        <input
          type='search'
          className='searchInput'
          value={search}
          onChange={(e) => setSearchValue(e.target.value)}
        />
      </div>
      <main>
        {articles && (
          <div className='articles'>
            {articles.map((article, index) => (
              <article key={index}>
                <img src={article.media} alt='article' width={400} />
                <div className='text'>
                  <h3>{article.title}</h3>
                  <p>{article.description}</p>
                  <Link to={`/fullstory/${index}`}>Full Story</Link>
                </div>
              </article>
            ))}
            ;
          </div>
        )}
      </main>
    </div>
  );
}

export default App;
