import { BiSearch } from 'react-icons/bi';
import React, { useState } from 'react';
import './Navbar.css';
import { Link } from 'react-router-dom';

function Navbar({ cb }) {
  const [searchValue, setSearchValue] = useState('');

  async function updateData(search) {
    const response = await fetch(
      `https://free-news.p.rapidapi.com/v1/search?q=${search}&lang=en&page_size=10`,
      {
        method: 'GET',
        headers: {
          'x-rapidapi-host': 'free-news.p.rapidapi.com',
          'x-rapidapi-key':
            '99904098f4msh9256c1370a1059dp15ba00jsn7a23af031e17',
        },
      }
    );
    const data = await response.json();
    const sorted = data.articles.sort(
      (a, b) => new Date(b.published_date) - new Date(a.published_date)
    );
    console.log(sorted);
    cb(data.articles);
  }

  function handleClick(e) {
    e.preventDefault();
    const search = document.querySelector('.search');
    console.log(search.value);
    updateData(search.value);
  }
  console.log(window.location.pathname);

  return (
    <div className='navBar'>
      <h1>TechNewsie</h1>
      {window.location.pathname === '/stories' ? (
        <form action=''>
          <input
            type='text'
            value={searchValue}
            className='search'
            onChange={(e) => setSearchValue(e.target.value)}
          />
          <button onClick={handleClick}>
            <BiSearch />
          </button>
        </form>
      ) : (
        <Link className='storiesLink' to='/stories'>
          Stories
        </Link>
      )}
    </div>
  );
}

export default Navbar;
