import React, { useState, useEffect } from 'react';
import './App.css';
import { Link } from 'react-router-dom';

function App() {
  const [articles, setArticles] = useState(null);
  const [searchValue, setSearchValue] = useState('tesla');
  const [search, setSearch] = useState('tesla');

  useEffect(() => {
    getData();
  });

  async function getData() {
    const key = '85062f74bccf4b33872bf17c11caaae6';

    const url = `https://community-open-weather-map.p.rapidapi.com/weather?q=${search}}&lat=0&lon=0&callback=test&id=2172797&lang=null&units=imperial&mode=xml`;

    const response = await fetch(url, {
      method: 'GET',
      headers: {
        'x-rapidapi-host': 'community-open-weather-map.p.rapidapi.com',
        'x-rapidapi-key': '99904098f4msh9256c1370a1059dp15ba00jsn7a23af031e17',
      },
    });
    const data = await response.json();

    setArticles(data.articles);
    console.log(data.articles);
  }

  function handleClick() {
    setSearch(searchValue);
    getData();
  }

  return (
    <div className='App'>
      <div className='search'>
        <input
          type='search'
          className='searchInput'
          value={search}
          onChange={(e) => setSearchValue(e.target.value)}
        />
        <button onClick={handleClick()}>Search</button>
      </div>
      <main>
        {articles && (
          <div className='articles'>
            {articles.map((article, index) => (
              <article key={index}>
                <div>{article.speed}</div>
                {/* <img src={article.urlToImage} alt='article' width={400} />
                <div className='text'>
                  <h3>{article.title}</h3>
                  <p>{article.description}</p>
                  <Link to={`/fullstory/${index}`}>Full Story</Link>
                </div> */}
              </article>
            ))}
            ;
          </div>
        )}
      </main>
    </div>
  );
}

export default App;
